package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b=(a%2);
		if(b==0)
		{
			System.out.println("Even");
		}
		else                                                                                                  
		{
			System.out.println("Odd");
		}
				
				
		

	}

}
